package javaH;

public class Ex2_1 {
	
	public static int sum(int n, int m) {
		return n + m;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 20;
		int s;
		char a;
		
		s = sum(i, 10);
		a = '?';
		System.out.println(a);
		System.out.println("HELLO");
		System.out.println(s);

	}

}
